    def test_skewness(self):
        """
        Tests if the returned data type is correct and if exceptions are being raised.
        """
        result_pandas = skewness(self.pandas_data, sampling_frequency=1000)
        result_numpy = skewness(self.numpy_data, sampling_frequency=1000)

        self.assertIsInstance(result_pandas, float)
        self.assertIsInstance(result_numpy, float)

        with self.assertRaises(Exception):
            skewness(self.int_data, sampling_frequency=1000)

        with self.assertRaises(Exception):
            skewness(self.pandas_data, sampling_frequency=1000, mode='min')

